{
  "umlContent": "classDiagram
    class User {
        +UUID id
        +String username
        +String email
        +UserStatus status
        +validateEmail()
    }
    class Post {
        +UUID id
        +UUID authorId
        +String title
        +String content
        +publish()
    }
    User "1" --> "*" Post",
  "packageName": "com.example.blog",
  "language": "java"
}

